import React from 'react';
import { Link } from 'react-router-dom';
import { Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const useStyles = makeStyles({
  root: {
    width: 500,
  },
});

const Sidebar = () => {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const sideBarStyles = {width: '20%', backgroundColor: '#f1efef', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', position: 'sticky', top: '0', left: '0', height: '100vh'}
    return (
        <div style={sideBarStyles}>
            {/* <Link style={{textDecoration: 'none'}} to={'/'} activeClassName="selected">
                <Typography>Sample Products</Typography>
            </Link>
            <Link style={{textDecoration: 'none'}} to={'/products'}>
                <Typography>All Products</Typography>
            </Link>
            <Link style={{textDecoration: 'none'}} to={'/cart'}>
                <Typography>Your Cart</Typography>
            </Link>
            <Link style={{textDecoration: 'none'}} to={'/review'}>
                <Typography>Order Review</Typography>
            </Link> */}
            <BottomNavigation
            value={value}
            onChange={(event, newValue) => {
                setValue(newValue);
            }}
            showLabels
            className={classes.root}
            >
                <BottomNavigationAction label="Recents" icon={<RestoreIcon />} /><br/>
                <BottomNavigationAction label="Favorites" icon={<FavoriteIcon />} />
                <BottomNavigationAction label="Nearby" icon={<LocationOnIcon />} />
            </BottomNavigation>
        </div>
    );
};

export default Sidebar;
