import React from 'react';
import { Link } from 'react-router-dom';
import { Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import HomeIcon from '@material-ui/icons/Home';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import LocalMallIcon from '@material-ui/icons/LocalMall';
import ComputerIcon from '@material-ui/icons/Computer';
const useStyles = makeStyles({
  root: {
    display: 'flex',
    flexDirection: 'column', 
    justifyContent: 'center',
    backgroundColor: '#f1f1f1',
    margin: '20px 0',
    padding: '20px 0'
  },
});

const Sidebar = () => {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const sideBarStyles = {
            width: '20vw',
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            backgroundColor: '#f1f1f1'
        }
    return (
        <div style={sideBarStyles}>
            <BottomNavigation
            value={value}
            onChange={(event, newValue) => {
                setValue(newValue);
            }}
            showLabels
            className={classes.root}
            >
                <BottomNavigationAction component={Link} to="/" label="Sample Products" icon={<HomeIcon />} />
                <BottomNavigationAction component={Link} to="/products" label="All Products" icon={<ComputerIcon />} />
                <BottomNavigationAction component={Link} to="/cart" label="Your Cart" icon={<AddShoppingCartIcon />} />
                <BottomNavigationAction component={Link} to="/review" label="Order Review" icon={<LocalMallIcon />} />
            </BottomNavigation>
        </div>
    );
};

export default Sidebar;
