import React from 'react';
import { Link } from 'react-router-dom';
import { Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const useStyles = makeStyles({
  root: {
    width: '100%',
    height: '50%',
    display: 'flex',
    flexDirection: 'column', 
    alignItems: 'center', 
    justifyContent: 'center',
    backgroundColor: '#f1efef'
  },
});

const Sidebar = () => {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const sideBarStyles = {width: '20%', backgroundColor: '#f1efef', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', position: 'sticky', top: '0', left: '0', height: '100vh'}
    return (
        <div style={sideBarStyles}>
            <BottomNavigation
            value={value}
            onChange={(event, newValue) => {
                setValue(newValue);
            }}
            showLabels
            className={classes.root}
            >
                <Link style={{textDecoration: 'none'}} to={'/'}>
                    <BottomNavigationAction label="Sample Products" icon={<RestoreIcon />} />
                </Link>
                <Link style={{textDecoration: 'none'}} to={'/products'}>
                    <BottomNavigationAction label="All Products" icon={<FavoriteIcon />} />
                </Link>
                <Link style={{textDecoration: 'none'}} to={'/cart'}>
                    <BottomNavigationAction label="Your Cart" icon={<LocationOnIcon />} />
                </Link>
                <Link style={{textDecoration: 'none'}} to={'/review'}>
                    <BottomNavigationAction label="Order Review Cart" icon={<LocationOnIcon />} />
                </Link>
            </BottomNavigation>
        </div>
    );
};

export default Sidebar;
