import React, {useEffect} from 'react';
import {useParams} from 'react-router-dom';
import Product from '../Product/Product';
import { products } from '../../asset/index';
const Details = ({handleAddToCart, cart, setCart}) => {
    useEffect(() =>{setCart([...cart])}, [cart])
    let {productKey} = useParams();
    productKey = productKey && 1;
    const product = products.find(pd => pd.pdKey === productKey);
    return (
        <div style={{height: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
            <Product handleAddToCart={handleAddToCart} showDetail={false} key={productKey} product={product}></Product>
        </div>
    );
};

export default Details;
