import React, {useState} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import App from './App';
import Header from './components/Header/Header';
import Products from './components/Products/Products';
import Navbar from './components/Navbar/Navbar';
import Details from './components/Details/Details';
import Cart from './components/Cart/Cart';
import Review from './components/Review/Review';
import { products } from './asset/index';
const Routes = () => {
    const [products, setProducts] = useState(products)
    const [cart, setCart] = useState([])
    useEffect(() =>{
        const handleAddToCart = (productKey) =>{
            const newCartProduct = products.find(p => p.pdKey === productKey);
            setCart([...cart, newCartProduct]);
            console.log(cart);
        }
    }, [count])
    return (
        <Router>
            <Header></Header>
            <Switch>
                <Route exact path="/"><App handleAddToCart={handleAddToCart}></App></Route>
                <Route exact path="/products"><Products handleAddToCart={handleAddToCart}></Products></Route>
                <Route exact path="/details/:productKey" component={Details}></Route>
                <Route exact path="/cart" component={Cart}></Route>
                <Route exact path="/review" component={Review}></Route>
            </Switch>
            <Navbar></Navbar>
        </Router>
    );
};

export default Routes;
