import React, {useState, useEffect} from 'react';
import Products from '../Products/Products';
import CartTotal from '../CartTotal/CartTotal';
import { Link } from 'react-router-dom';

const Cart = ({cart, setCart}) => {
    const cartStyles = {
        display: 'flex',
        maxWidth: '100vw',
    }
    const handlePlus = (productKey) =>{
        const product = cart.find(pd => pd.pdKey === productKey);
        product.count++;
    }
    const handleMinus = (productKey) =>{
        const product = cart.find(pd => pd.pdKey === productKey);
        product.count > 1 ? product.count-- : product.count = 1;
    }
    useEffect(() => {
        setCart([...cart])
    }, [cart])
    return (
        <div style={cartStyles}>
            {
                !cart.length ? (<h1>Your Cart is Empty!! Please <Link to={'/products'}>purchase something.</Link> Have a good day </h1>) : <Products cartHandle={true} handleMinus={handleMinus} handlePlus={handlePlus} products={cart}></Products>
            }
            <CartTotal></CartTotal>
        </div>
    );
};

export default Cart;
