import React, {useState} from 'react';
import Products from '../Products/Products';
import CartTotal from '../CartTotal/CartTotal';

const Cart = ({cart}) => {
    const cartStyles = {
        display: 'flex',
        maxWidth: '100vw',
    }
    const handlePlus = (productKey) =>{
        const product = cart.find(pd => pd.pdKey === productKey);
        product.count++;
    }
    const handleMinus = (productKey) =>{
        const product = cart.find(pd => pd.pdKey === productKey);
        product.count > 1 ? product.count-- : product.count = 1;
    }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
    return (
        <div style={cartStyles}>
            <Products cartHandle={true} handleMinus={handleMinus} handlePlus={handlePlus} products={cart}></Products>
            <CartTotal></CartTotal>
        </div>
    );
};

export default Cart;
